<?php/** * This is the model class for table "cms_page". * * The followings are the available columns in table 'cms_page': * @property integer $id * @property string $key * @property string $title * @property string $content */class Page extends CActiveRecord{    /**     * Returns the static model of the specified AR class.     * @return Page the static model class     */    public static function model($className = __CLASS__)    {        return parent::model($className);    }    /**     * @return string the associated database table name     */    public function tableName()    {        return '{{page}}';    }    /**     * @return array validation rules for model attributes.     */    public function rules()    {        // NOTE: you should only define rules for those attributes that        // will receive user inputs.        return array(            array('category_id, key, title, content', 'required'),            array('key, title', 'length', 'max' => 200),            array('language', 'safe'),            // The following rule is used by search().            // Please remove those attributes that should not be searched.            array('id, key, title, content', 'safe', 'on' => 'search'),        );    }    /**     * @return array relational rules.     */    public function relations()    {        // NOTE: you may need to adjust the relation name and the related        // class name for the relations automatically generated below.        return array(            'category' => array(self::BELONGS_TO, 'Category', 'category_id'),        );    }    /**     * @return array customized attribute labels (name=>label)     */    public function attributeLabels()    {        return array(            'id' => 'ID',            'category_id' => '分类',            'category.name' => '分类',            'key' => '单页URL',            'title' => '标题',            'content' => '内容',            'language' => '语言',        );    }    /**     * Retrieves a list of models based on the current search/filter conditions.     * @return CActiveDataProvider the data provider that can return the models based on the search/filter conditions.     */    public function search()    {        // Warning: Please modify the following code to remove attributes that        // should not be searched.        $criteria = new CDbCriteria;        $criteria->compare('id', $this->id);        $criteria->compare('key', $this->key, true);        $criteria->compare('title', $this->title, true);        $criteria->compare('content', $this->content, true);        return new CActiveDataProvider(get_class($this), array(            'criteria' => $criteria,        ));    }    public function storeSearch()    {        // Warning: Please modify the following code to remove attributes that        // should not be searched.        $criteria = new CDbCriteria;        $criteria->condition = "store_id = " . $_SESSION['store']['store_id'];        $criteria->order = "id desc";        $criteria->compare('id', $this->id);        $criteria->compare('key', $this->key, true);        $criteria->compare('title', $this->title, true);        $criteria->compare('content', $this->content, true);        return new CActiveDataProvider(get_class($this), array(            'criteria' => $criteria,        ));    }}